import React from 'react';
import "./Banner.css"
function Banner(){
    function truncate(string, numbeOfCharachters){
        return string?.length> numbeOfCharachters ? string.substr(0, numbeOfCharachters-1)+'...':string;
    }
    return(
        <header className="banner" style={{
            backgroundSize:"cover",
            backgroundImage:`url("https://i.imgur.com/e1hLQ2m.png")`,
            backgroundPosition: "center center",
        }}>
            <div className="banner_contents">
                <h1 className="banner_title">Movie Name</h1>
                <div className="banner_buttons">
                <button className="banner_button">Play</button>
                <button className="banner_button">My list</button>
                </div>
                <h1 className="banner_description">J{truncate(`this is just description for movie or series`)}</h1>  
            </div>
          <div className="fade_bottom"/>
        </header>
    )
}
export default Banner;
